import React, { useEffect, useState } from 'react';
import MovieList from './MovieList';
import SearchBar from './SearchBar';
import AddMovie from './AddMovie';
import EditMovie from './EditMovie';
import axios from 'axios';
import { Movies } from './Movies'
// import { BrowserRouter as Router, Routes, Route } from "react-router-dom";



const App = () => {


  const [state, setState] = useState([]);
  const[search ,setSearch ] = useState('')

 useEffect(()=>{
   loadMoviesData();
 },[]);
   const loadMoviesData = async () => {
     return await axios.get("http://localhost:3002/movies")
     .then((response) => setState(response.state))
     .catch((err)=>console.log(err))



    // const data= response.json();
    // console.log(data)
  }
  console.log("states",state)

  const Search = (mov) => {
    return mov.filter((movies) => movies.name.toLowerCase().includes(search))

  }

  const deleteMovie = (id) => {
    setState(state.filter((state) => state !== id))
    // console.log(id)
  }

  return (
    <div className='container'>
      <div className='row'>
        <div className='col-md-12'>
          <SearchBar setSearch={setSearch} />
        </div>
      </div>
      <MovieList movies={Search(state)} deleteMovie={deleteMovie}/>
      {/* <MovieList movies={Movies} /> */}
    </div>
  );
};

export default App;
