import React,{useState} from 'react';
import {Movies} from './Movies'


const MovieList = (props) => {

    const [state, setState]  = useState(Movies)

    const deleteMovie = (id) => {
        setState(state.filter((state) => state !== id))
        // console.log(id)
      }
 

    return (
        <div className='row'>

            {props.movies.filter((val)=>{ 
                if(searchQuery==""){
                    return val
                }
                else if( val.name.toLoverCase().includes(searchQuery.toLoverCase())){
                    return val
                }
            }).map((movie) => (
                <div className='col-md-4' key={movie.id}>
                <div className='card mb-4 shadow-sm '>
                    <img src={movie.imageURL} className='card-img-top' alt="Semple Movie" />
                     <div className='card-body'>
                        <h5 className='card-title'>{movie.name} </h5>
                        <p className='card-text'> {movie.overview} </p>
                        <div className='d-flex justify-content-between align-items-center'>
                            <button className='btn btn-md btn-outline-danger' onClick={ (event)=> deleteMovie(movie)} > Delete</button>
                            <h2> <span className='badge badge-info' > {movie.rating} </span> </h2>
                        </div>
                    </div>   
                </div>

            </div>

            ))}
            
        </div>
    );
};

export default MovieList;
