import MovieList from './MovieList';
import SearchBar from './SearchBar';
import AddMovie from './AddMovie';
import EditMovie from './EditMovie';
import axios from 'axios';
import { Movies } from './Movies'
// import { BrowserRouter as Router, Routes, Route } from "react-router-dom";



const App = () => {
  // const [count, setCount] = useState(0);

  // useEffect(() => {
  //   setTimeout(() => {
  //     setCount((count) => count + 1);
  //   }, 1000);
  // });

  // return <h1>I have rendered {count} times!</h1>;


  const [state, setState] = useState([]);
  const [search, setSearch] = useState('')

  useEffect(() => {
    loadMoviesData();
  }, []);

  const loadMoviesData = async () => {

    // return await axios
    // .get("http://localhost:3002/movie")
    //   .then((response) => setState(response.state))
    //   .catch((err) => console.log(err))


    const baseURl = "http://localhost:3002/movie";
    const response = await fetch(baseURl);
    console.log(response)
    const data = await response.json()
    console.log(data)
    setState(data)
  }

  // console.log("states", state)

  const Search = (mov) => {
    return mov.filter((state) => state.name.toLowerCase().includes(search))

  }

  const deleteMovie = (id) => {
    setState(state.filter((state) => state !== id))
    // console.log(id)
  }

  return (
    <div className='container'>
      <div className='row'>
        <div className='col-md-12'>
          <SearchBar setSearch={setSearch} />
        </div>
      </div>
      <MovieList movies={Search(state)} deleteMovie={deleteMovie} />
      {/* <MovieList movies={Movies} /> */}
    </div>
  );
};

export default App;
