import React from 'react';

const MovieList = (props) => {


 

    return (
        <div className='row'>

            {props.movies.filter((mov)=>mov.name.toLoverCase().includes(props.SearchQuery)).map((movie) => (
                <div className='col-md-4' key={movie.id}>
                <div className='card mb-4 shadow-sm '>
                    <img src={movie.imageURL} className='card-img-top' alt="Semple Movie" />
                     <div className='card-body'>
                        <h5 className='card-title'>{movie.name} </h5>
                        <p className='card-text'> {movie.overview} </p>
                        <div className='d-flex justify-content-between align-items-center'>
                            <button className='btn btn-md btn-outline-danger' onClick={ (event)=>props.deleteMovie(movie)} > Delete</button>
                            <h2> <span className='badge badge-info' > {movie.rating} </span> </h2>
                        </div>
                    </div>   
                </div>

            </div>

            ))}
            
        </div>
    );
};

export default MovieList;
